    /**
     * 匹配
     *
     * @return
     */
    public int match1(char[] array, int start, LinkedList<String> words) {

        // 匹配一个单词

        int j = 0;
        Iterator<String> iterator = words.iterator();
        while (iterator.hasNext()) {
            String word = iterator.next();
            boolean flag = true;

            for (int i = 0; i < word.length(); i++) {
                if (start + i >= array.length || array[start + i] != word.charAt(i)) {
                    flag = false;
                    break;
                }
            }

            // 当前单词匹配成功
            if (flag) {
                iterator.remove();
                if (words.size() != 0) {
                    return match1(array, start + word.length(), words);
                }
                // 己经完全匹配了，这时候返回成功的座标，需求是要开始的坐标，所以需要把成功的坐标减去字符串总长度
                return start + word.length();
            }
        }

        // 一个单词都没有成功
        return -1;
    }


